
public class loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 40;
		int j = 30;
		int k = 10;
		
		if(i>j & i>k) {
			
			System.out.println("Greater number is i:"+i);
		}
		else if(j>k & j>i) {
			System.out.println("Greater number is j:"+j);
		}
		else {
			System.out.println("Greater number is k:"+k);
		}
		
	if(i==j) {
		System.out.println("i is equal to j");
	}
	else
	{
	 System.out.println("i is not equal to j");
	}

	}
	

}
